package sshr.servicemodel;import sshr.domainmodel.BaseEntity;import java.util.List;public interface CrudService {	public <T extends BaseEntity> T add(T entity) throws GenericCrudServiceException;		public <T extends BaseEntity> T update(T entity) throws GenericCrudServiceException;	public <T extends BaseEntity, U extends Number> void remove(Class<T> eClass, U id) throws GenericCrudServiceException;	public <T extends BaseEntity, U extends Number> T find(Class<T> eClass, U id) throws GenericCrudServiceException;	public <T extends BaseEntity> List<T> findAll(Class<T> entityClass) throws GenericCrudServiceException;}