package sshr.asistencia;import java.util.*;import sshr.domainmodel.*;import sshr.domainmodel.asistencia.*;import sshr.asistencia.*;public final class AsistenciaApplication {	private static AsistenciaCrudService _crudService;		static {		try {				_crudService = new AsistenciaCrudService();				} catch (Exception ex) { }	}		/*		SECTION: CRUD[PERFIL-HORARIO]			*/		public static PerfilHorario createPerfilHorario(String desc, String codigo, String nombre) throws AsistenciaException {					try {					PerfilHorario ph = EntityFactory.getNewEntity(PerfilHorario.class);					ph.setDesc(desc);					ph.setCodigo(codigo);					ph.setNombre(nombre);					ph = _crudService.add( ph );					if (ph == null) 						throw new AsistenciaException("No se pudo crear el Perfil de Horario.");										return ph;								} catch (Exception ex) { 									throw new AsistenciaException("An error has occurred, please review the log.", ex);			}		}			public static PerfilHorario updatePerfilHorario(Long id, String desc, String codigo, String nombre) throws AsistenciaException {					try {					PerfilHorario ph = _crudService.find(PerfilHorario.class, id);					ph.setDesc(desc);					ph.setCodigo(codigo);					ph.setNombre(nombre);										ph = _crudService.update( ph );					if (ph == null) 						throw new AsistenciaException("No se pudo actualizar el Perfil de Horario.");																	return ph;								} catch (Exception ex) { 									throw new AsistenciaException("An error has occurred, please review the log.", ex);			}		}		public static List<PerfilHorario> findAllPerfilHorario() throws AsistenciaException {					try {					return _crudService.findAll(PerfilHorario.class);								} catch (Exception ex) { 									throw new AsistenciaException("An error has occurred, please review the log.", ex);			}		}		public static PerfilHorario findPerfilHorario(Long id) throws AsistenciaException {					try {					return _crudService.find(PerfilHorario.class, id);								} catch (Exception ex) { 									throw new AsistenciaException("An error has occurred, please review the log.", ex);			}		}		public static void removePerfilHorario(Long id) throws AsistenciaException {					try {					_crudService.remove(PerfilHorario.class, id);								} catch (Exception ex) { 									throw new AsistenciaException("An error has occurred, please review the log.", ex);			}		}	/*		SECTION: CRUD[TIPO-DIA]			*/		public static TipoDia createTipoDia(String desc, String codigo, String nombre) throws AsistenciaException {					try {					TipoDia td = EntityFactory.getNewEntity(TipoDia.class);					td.setDesc(desc);					td.setCodigo(codigo);					td.setNombre(nombre);					td = _crudService.add( td );					if (td == null) 						throw new AsistenciaException("No se pudo crear el Perfil de Horario.");										return td;								} catch (Exception ex) { 									throw new AsistenciaException("An error has occurred, please review the log.", ex);			}		}			public static TipoDia updateTipoDia(Long id, String desc, String codigo, String nombre) throws AsistenciaException {					try {					TipoDia td = _crudService.find(TipoDia.class, id);					td.setDesc(desc);					td.setCodigo(codigo);					td.setNombre(nombre);										td = _crudService.update( td );					if (td == null) 						throw new AsistenciaException("No se pudo actualizar el Perfil de Horario.");																	return td;								} catch (Exception ex) { 									throw new AsistenciaException("An error has occurred, please review the log.", ex);			}		}		public static List<TipoDia> findAllTipoDia() throws AsistenciaException {					try {					return _crudService.findAll(TipoDia.class);								} catch (Exception ex) { 									throw new AsistenciaException("An error has occurred, please review the log.", ex);			}		}		public static TipoDia findTipoDia(Long id) throws AsistenciaException {					try {					return _crudService.find(TipoDia.class, id);								} catch (Exception ex) { 									throw new AsistenciaException("An error has occurred, please review the log.", ex);			}		}		public static void removeTipoDia(Long id) throws AsistenciaException {					try {					_crudService.remove(TipoDia.class, id);								} catch (Exception ex) { 									throw new AsistenciaException("An error has occurred, please review the log.", ex);			}		}			/*		SECTION: CRUD[PERIODO]			*/		public static Periodo createPeriodo(String nombre, String desc, String bitacora, Date fechaInicio, Date fechaTermino) throws AsistenciaException {					try {					Periodo e = EntityFactory.getNewEntity(Periodo.class);					e.setDesc(desc);					e.setBitacora(bitacora);					e.setNombre(nombre);					e.setFechaInicio(fechaInicio);					e.setFechaTermino(fechaTermino);										e = _crudService.add( e );					if (e == null) 						throw new AsistenciaException("No se pudo crear el Perfil de Horario.");										return e;								} catch (Exception ex) { 									throw new AsistenciaException("An error has occurred, please review the log.", ex);			}		}			public static Periodo updatePeriodo(Long id, String desc, String bitacora, String nombre) throws AsistenciaException {					try {					Periodo td = _crudService.find(Periodo.class, id);					td.setDesc(desc);					td.setBitacora(bitacora);					td.setNombre(nombre);										td = _crudService.update( td );					if (td == null) 						throw new AsistenciaException("No se pudo actualizar el Perfil de Horario.");																	return td;								} catch (Exception ex) { 									throw new AsistenciaException("An error has occurred, please review the log.", ex);			}		}		public static List<Periodo> findAllPeriodo() throws AsistenciaException {					try {					return _crudService.findAll(Periodo.class);								} catch (Exception ex) { 									throw new AsistenciaException("An error has occurred, please review the log.", ex);			}		}		public static Periodo findPeriodo(Long id) throws AsistenciaException {					try {					return _crudService.find(Periodo.class, id);								} catch (Exception ex) { 									throw new AsistenciaException("An error has occurred, please review the log.", ex);			}		}		public static void removePeriodo(Long id) throws AsistenciaException {					try {					_crudService.remove(Periodo.class, id);								} catch (Exception ex) { 									throw new AsistenciaException("An error has occurred, please review the log.", ex);			}		}}